// The Cart state: stores item ID, quantity, and a unique identifier for custom items
let cart = {};
const TAX_RATE = 0.12; 
const DISCOUNT_RATE = 0.20; // 20% Senior/PWD discount
const CURRENCY = '';
let customItemIdCounter = 1000; 

// --- DOM ELEMENTS ---
const menuElement = document.getElementById('menu');
const cartElement = document.getElementById('cart');
const emptyCartMessage = document.getElementById('empty-cart-message');
const subtotalDisplay = document.getElementById('subtotalDisplay');
const taxDisplay = document.getElementById('taxDisplay');
const totalDisplay = document.getElementById('totalDisplay');
const discountRow = document.getElementById('discountRow');
const discountDisplay = document.getElementById('discountDisplay');
const categoryTitleElement = document.getElementById('categoryTitle'); // New element
const tabsContainer = document.querySelector('.category-tabs'); // New container

// --- FUNCTIONS ---

/**
 * Changes the active category and re-renders the tabs and menu.
 * @param {string} category 
 */
function changeCategory(category) {
    activeCategory = category;
    renderTabs(); 
    renderMenu(); 
}

/**
 * Renders the category tabs at the top of the menu.
 */
function renderTabs() {
    tabsContainer.innerHTML = '';
    
    menuCategories.forEach(category => {
        const isActive = category === activeCategory;
        const buttonClass = isActive
            ? 'px-4 py-2 bg-primary text-white font-bold rounded-lg shadow-md transition duration-150'
            : 'px-4 py-2 bg-gray-100 text-darkbg font-semibold rounded-lg hover:bg-gray-200 transition duration-150';
            
        const button = document.createElement('button');
        // FIX: Added type="button" to prevent unwanted form submissions/scroll jumps
        button.setAttribute('type', 'button'); 
        button.className = buttonClass;
        button.textContent = category;
        // Important: Use the changeCategory function for clicks
        button.setAttribute('onclick', `changeCategory('${category}')`);
        tabsContainer.appendChild(button);
    });
}


/**
 * Renders the menu list filtered by the active category.
 */
function renderMenu() {
    menuElement.innerHTML = '';
    
    // 1. Determine Layout Class (Now applied universally to ALL categories)
    const layoutClass = 'grid grid-cols-2 gap-4 md:grid-cols-3'; 
    
    // Apply the layout class to the menu container
    menuElement.className = layoutClass; 
    
    // 2. Filter the menu based on the active category
    const filteredMenu = menu.filter(item => item.category === activeCategory);
    
    // 3. Update the Category Title
    categoryTitleElement.textContent = activeCategory + ' (Tap to Choose)';

    // 4. Render items
    filteredMenu.forEach(item => {
        const itemDiv = document.createElement('div');
        itemDiv.setAttribute('onclick', `selectItem(${item.id})`);
        
        // Use the horizontal card layout (previously only for Desserts) for ALL items
        itemDiv.className = 'flex flex-col items-center justify-start p-4 bg-white rounded-lg shadow-md border border-gray-200 transition-all duration-150 menu-item-card text-center min-h-[180px]';
        itemDiv.innerHTML = `
            <img src="${item.imageUrl}" alt="${item.name}" 
                 class="w-20 h-20 object-cover rounded-md shadow-sm border border-gray-100 mb-2"
                 onerror="this.onerror=null; this.src='https://placehold.co/100x100/94a3b8/ffffff?text=Image+Missing';">
            <span class="text-gray-800 font-bold block text-sm">${item.name}</span>
            <span class="text-lg text-secondary font-extrabold mt-1">${CURRENCY}${item.price.toFixed(2)}</span>
            ${item.isCustomizable ? '<span class="text-xs text-primary block mt-1 font-semibold">Tap for Options</span>' : ''}
        `;
        
        menuElement.appendChild(itemDiv);
    });
}

/**
 * Determines whether to show the customization modal or add the item directly.
 * @param {number} itemId 
 */
function selectItem(itemId) {
    const item = menu.find(i => i.id === itemId);
    if (item && item.isCustomizable) {
        openCustomizationModal(item);
    } else if (item) {
        // Now opens the Info Modal for all simple items
        openInfoModal(item);
    }
}

/**
 * Adds a simple, non-customizable item to the cart.
 * @param {object} item - The menu item object.
 */
function addToCartSimple(item) {
    // Use original ID as cart key
    const itemKey = item.id.toString();
    // Find an existing entry if it's a simple item
    const existingEntry = cart[itemKey];

    if (existingEntry) {
            existingEntry.count += 1;
    } else {
            cart[itemKey] = { count: 1, item: item };
    }
    renderCart();
}

/**
 * NEW: Generates and shows the modal for non-customizable items (for description/info only).
 * @param {object} item - The item to display.
 */
function openInfoModal(item) {
    
    // Reusing the description block structure
    const descriptionBlock = `
        <div class="bg-gray-100 p-4 rounded-lg mb-6 border-l-4 border-secondary">
            <p class="text-sm italic text-darkbg font-semibold">${item.description || "Authentic Filipino dish."}</p>
            <p class="text-xs text-gray-700 mt-2">
                <span class="font-bold">Key Ingredients:</span> ${item.ingredients ? item.ingredients.join(', ') : 'Not specified.'}
            </p>
        </div>
    `;
    
    const infoHtml = `
        <div class="modal-body-wrapper">
            ${descriptionBlock}
            <div class="mt-6 text-center">
                <span class="text-xl font-bold text-darkbg">Price: </span>
                <span class="text-3xl font-extrabold text-primary">${CURRENCY}${item.price.toFixed(2)}</span>
            </div>
        </div>
    `;
    
    // Show modal, and on Confirm, add the item to the cart
    showCustomModal(`${item.name} Details`, infoHtml, (confirmed) => {
        if (confirmed) {
            addToCartSimple(item);
        }
    }, true); // Use true for custom content
    
    // Temporarily change the Confirm button text for clarity in this modal
    const confirmBtn = document.getElementById('modalConfirmBtn');
    confirmBtn.textContent = `Add 1 ${item.name}`;
    
}

/**
 * Generates the HTML content for the customization modal.
 * @param {object} item - The item to customize.
 * @returns {string} HTML string.
 */
function generateCustomizationModalHTML(item) {
    const basePrice = item.customOptions.basePrice;
    
    // --- Description and Ingredients Block ---
    const descriptionBlock = `
        <div class="bg-gray-100 p-4 rounded-lg mb-6 border-l-4 border-secondary">
            <p class="text-sm italic text-darkbg font-semibold">${item.description || "Authentic Filipino dish."}</p>
            <p class="text-xs text-gray-700 mt-2">
                <span class="font-bold">Key Ingredients:</span> ${item.ingredients ? item.ingredients.join(', ') : 'Not specified.'}
            </p>
        </div>
    `;
    // ---------------------------------------------

    let html = `
        <div class="modal-body-wrapper">
            
            ${descriptionBlock} <p class="text-gray-600 mb-4 font-semibold">Select your preference:</p>
            <div id="option-list" class="space-y-3">
    `;

    item.customOptions.options.forEach((option, index) => {
        const finalPrice = basePrice + option.priceDelta;
        const priceText = `(Total: ${CURRENCY}${finalPrice.toFixed(2)})`;
        const radioId = `option-${item.id}-${index}`;
        
        html += `
            <label for="${radioId}" class="custom-option-label flex items-center justify-between p-4 bg-white rounded-md shadow-sm cursor-pointer hover:bg-gray-50 transition duration-100">
                <div class="flex flex-col">
                    <span class="font-bold text-lg text-gray-800">${option.label}</span>
                    <span class="text-sm text-gray-500">${option.description}</span>
                </div>
                <div class="flex items-center gap-3">
                    <span class="text-lg font-extrabold text-primary">${priceText}</span>
                    <input type="radio" id="${radioId}" name="combo-option" value="${index}" 
                       class="form-radio h-6 w-6 text-primary focus:ring-primary"
                       data-price="${finalPrice.toFixed(2)}"
                       data-description="${option.description}"
                       ${index === item.customOptions.defaultChoice ? 'checked' : ''}>
                </div>
            </label>
        `;
    });

    html += `
            </div>
            
        </div>
        <div class="mt-6 text-center border-t pt-4">
            <span class="text-xl font-bold text-darkbg">Final Price: </span>
            <span id="modal-final-price" class="text-3xl font-extrabold text-primary"></span>
        </div>
    `;
    return html;
}

/**
 * Shows the customization modal for a specific item.
 * @param {object} item - The item to customize.
 */
function openCustomizationModal(item) {
    const customContentHtml = generateCustomizationModalHTML(item);
    
    showCustomModal(`Customize ${item.name}`, customContentHtml, (confirmed) => {
        if (confirmed) {
            addToCartCustom(item);
        }
    }, true); // Pass true to enable custom HTML handling

    // Initial price update and radio button listener setup
    const modal = document.getElementById('customModal');
    const priceDisplay = document.getElementById('modal-final-price');
    
    // Note: Must wait for the modal to be visible before querying elements inside modalBody
    const updatePrice = () => {
        const checkedRadio = modal.querySelector('input[name="combo-option"]:checked');
        if (checkedRadio) {
            const price = parseFloat(checkedRadio.getAttribute('data-price'));
            priceDisplay.textContent = `${CURRENCY}${price.toFixed(2)}`;
        }
    };
    
    // Delay listener setup until after the modal is shown and populated
    setTimeout(() => {
        const radios = modal.querySelectorAll('input[name="combo-option"]');
        radios.forEach(radio => radio.addEventListener('change', updatePrice));
        updatePrice(); // Set initial price
    }, 50); 
}

/**
 * Adds the fully customized item to the cart using a unique ID.
 * @param {object} baseItem - The original menu item.
 */
function addToCartCustom(baseItem) {
    const modal = document.getElementById('customModal');
    const checkedRadio = modal.querySelector('input[name="combo-option"]:checked');

    if (checkedRadio) {
        const finalPrice = parseFloat(checkedRadio.getAttribute('data-price'));
        const description = checkedRadio.getAttribute('data-description');
        
        // Custom items always get a unique key, regardless of price/description to track them separately.
        const uniqueCartKey = `CUST-${customItemIdCounter++}`;
        
        const customItem = {
            name: `${baseItem.name} (${description})`,
            price: finalPrice,
            id: baseItem.id, // Store original ID for reference
            cartKey: uniqueCartKey // Unique key for cart management
        };
        
        // Add to cart dictionary using the unique key
        cart[uniqueCartKey] = { count: 1, item: customItem };
        renderCart();
    }
}


/**
 * Decrements the quantity of an item in the cart.
 * @param {string} cartKey - The unique key (original ID or CUST-ID) of the item to remove.
 */
function removeFromCart(cartKey) {
    const itemEntry = cart[cartKey];
    if (!itemEntry) return;

    // Only decrement the count if it's a simple item (key is just the ID string)
    if (itemEntry.count > 1 && !cartKey.startsWith('CUST-')) {
        itemEntry.count -= 1;
    } else {
        // For custom items (which are single entries) or simple items with count 1, delete the unique entry
        delete cart[cartKey];
    }
    renderCart();
}

/**
 * Clears the cart state and updates the UI.
 */
function clearOrder() {
    showCustomModal("Confirm Clear Order", "Are you sure you want to clear your entire order?", (confirmed) => {
        if(confirmed) {
            cart = {}; // Reset the cart object
            renderCart();
        }
    }, false); // Pass false for simple confirmation modal
}

/**
 * Calculates subtotal, discount, tax, and total based on the current cart state.
 * IMPORTANT: VAT is calculated on the VAT-exclusive price after discount.
 * @returns {object} {subtotal, vatExclusiveSubtotal, discountAmount, tax, total}
 */
function calculateTotals() {
    let subtotal = 0; // Total price including VAT
    
    for (const cartKey in cart) {
        if (cart.hasOwnProperty(cartKey)) {
            const entry = cart[cartKey];
            subtotal += entry.item.price * entry.count;
        }
    }

    // 1. Calculate VAT Exclusive Subtotal (The Price Before VAT)
    // Price = VAT Exclusive Price * (1 + VAT Rate)
    // VAT Exclusive Price = Price / (1 + VAT Rate)
    const vatExclusiveSubtotal = subtotal / (1 + TAX_RATE);
    
    // 2. Determine Discount
    const selectedDiscount = document.querySelector('input[name="discount"]:checked').value;
    let discountAmount = 0;
    
    if (selectedDiscount !== 'none') {
        // The 20% discount is applied to the VAT-exclusive subtotal.
        discountAmount = vatExclusiveSubtotal * DISCOUNT_RATE;
    }
    
    // 3. Calculate New VAT Exclusive Price (Net Price)
    const netVatExclusivePrice = vatExclusiveSubtotal - discountAmount;
    
    // 4. Calculate Final VAT
    // In the Philippines, Senior/PWD discounts exempt the user from the *12% VAT* on the discountable portion.
    // A simpler, standard approach is: New VAT = Net Price * VAT Rate
    const finalVat = netVatExclusivePrice * TAX_RATE;

    // 5. Calculate Total Payable
    const totalPayable = netVatExclusivePrice + finalVat;

    return { 
        subtotal, // Subtotal including original VAT (used for display only)
        vatExclusiveSubtotal, // Original price before VAT
        discountAmount, // 20% discount applied to the VAT-exclusive price
        tax: finalVat, // VAT applied to the net (discounted) price
        total: totalPayable 
    };
}

/**
 * Renders the cart items and updates the totals display.
 */
function renderCart() {
    const { subtotal, discountAmount, tax, total } = calculateTotals();

    cartElement.innerHTML = '';
    const cartKeys = Object.keys(cart).filter(key => cart[key].count > 0);

    // Toggle empty cart message visibility
    if (cartKeys.length === 0) {
        emptyCartMessage.style.display = 'block';
    } else {
        emptyCartMessage.style.display = 'none';

        cartKeys.forEach(cartKey => {
            const entry = cart[cartKey];
            const quantity = entry.count;
            const item = entry.item;
            const itemTotal = item.price * quantity;

            const cartItemDiv = document.createElement('div');
            // Use different border style for custom items to distinguish them in the cart
            const borderColor = cartKey.startsWith('CUST-') ? 'border-l-4 border-secondary' : 'border';
            
            cartItemDiv.className = `flex justify-between items-center text-gray-800 bg-white p-2 rounded ${borderColor}`;
            cartItemDiv.innerHTML = `
                <div class="flex items-center gap-3">
                    <button onclick="removeFromCart('${cartKey}')" 
                            class="bg-red-100 text-red-600 w-6 h-6 rounded-full text-xs font-bold hover:bg-red-200 transition">-</button>
                    <span class="font-medium">${item.name}</span>
                </div>
                <div class="text-right">
                    <span class="font-bold text-secondary">${CURRENCY}${itemTotal.toFixed(2)}</span>
                    <span class="text-sm text-gray-500 block">x${quantity}</span>
                </div>
            `;
            cartElement.appendChild(cartItemDiv);
        });
    }

    // --- Update Totals Display ---
    subtotalDisplay.textContent = `${CURRENCY}${subtotal.toFixed(2)}`;
    taxDisplay.textContent = `${CURRENCY}${tax.toFixed(2)}`;
    totalDisplay.textContent = `${CURRENCY}${total.toFixed(2)}`;
    
    if (discountAmount > 0.009) {
        discountRow.style.display = 'flex';
        discountDisplay.textContent = `-${CURRENCY}${discountAmount.toFixed(2)}`;
    } else {
        discountRow.style.display = 'none';
    }
}

// --- Custom Modal (Replaces alert/confirm) ---

/**
 * Sets up the HTML structure for the custom confirmation modal.
 */
function createModalHTML() {
    const modalHtml = `
        <div id="customModal" class="fixed inset-0 bg-gray-900 bg-opacity-50 flex items-center justify-center p-4 z-50 hidden">
            <div id="modalContentWrapper" class="bg-white rounded-xl shadow-xl w-full max-w-sm p-6 transform transition-all duration-300 scale-95 opacity-0">
                <h3 id="modalTitle" class="text-xl font-bold text-darkbg mb-3">Modal Title</h3>
                <div id="modalBody" class="text-gray-600 mb-6">Modal Body Content</div>
                <div class="flex justify-end space-x-3">
                    <button id="modalCancelBtn" class="px-4 py-2 bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300 transition">Cancel</button>
                    <button id="modalConfirmBtn" class="px-4 py-2 bg-primary text-white rounded-lg hover:bg-red-600 transition">Add to Cart</button>
                </div>
            </div>
        </div>
    `;
    document.body.insertAdjacentHTML('beforeend', modalHtml);
}

/**
 * Shows the custom modal with given content and confirmation callback.
 */
function showCustomModal(title, bodyContent, onComplete, isCustomContent) {
    const modal = document.getElementById('customModal');
    const modalContentWrapper = document.getElementById('modalContentWrapper');
    const modalTitle = document.getElementById('modalTitle');
    const modalBody = document.getElementById('modalBody');
    const confirmBtn = document.getElementById('modalConfirmBtn');
    const cancelBtn = document.getElementById('modalCancelBtn');

    modalTitle.textContent = title;
    
    // Set body content based on type
    if (isCustomContent) {
        modalBody.innerHTML = bodyContent;
        modalContentWrapper.classList.add('max-w-md'); // Make customization modal slightly wider
        // The confirm button text is set specifically inside openInfoModal or stays default "Add to Cart" for customization
    } else {
        modalBody.textContent = bodyContent;
        modalContentWrapper.classList.remove('max-w-md');
        confirmBtn.textContent = "Confirm";
    }

    // Reset and set handlers
    confirmBtn.onclick = null;
    cancelBtn.onclick = null;

    confirmBtn.onclick = () => {
        hideModal();
        onComplete(true); // Confirmed
    };
    cancelBtn.onclick = () => {
        hideModal();
        onComplete(false); // Cancelled
    };
    
    function hideModal() {
        modal.classList.add('hidden');
        modalContentWrapper.classList.remove('scale-100', 'opacity-100');
        modalContentWrapper.classList.add('scale-95', 'opacity-0');
    }

    // Show modal with animation
    modal.classList.remove('hidden');
    setTimeout(() => {
        modalContentWrapper.classList.remove('scale-95', 'opacity-0');
        modalContentWrapper.classList.add('scale-100', 'opacity-100');
    }, 10);
}

// --- INITIALIZATION ---
window.onload = function() {
    createModalHTML();
    renderTabs(); // Render tabs first
    renderMenu(); // Render initial menu
    renderCart(); // Initialize cart display (should show empty message)
};